"use strict";

const init = require('../utils/init');

exports.command = 'init';
exports.describe = 'Adds all required Layer0 dependencies and files to your app.';
exports.builder = {
  layer0Version: {
    type: 'string',
    describe: 'The Layer0 version to install.',
    default: 'latest'
  },
  skipLayer0Deps: {
    type: 'boolean',
    describe: 'If true, Layer0 packages will not be installed.'
  },
  connector: {
    type: 'string',
    describe: 'The name of a connector package to install and use to prepare your app for deployment on Layer0.'
  }
};

exports.handler = async ({
  context,
  layer0Version,
  skipLayer0Deps,
  connector
}) => {
  await init({
    context,
    version: layer0Version,
    skipLayer0Deps,
    connector
  });
};