"use strict";

const slash = require('slash');

const {
  join
} = require('path');

const {
  existsSync
} = require('fs');

module.exports = function getLayer0Config() {
  const file = [join(process.cwd(), 'layer0.config.js'), join(process.cwd(), 'layer0.config.cjs')].find(existsSync);

  if (file) {
    return require(slash(file));
  } else {
    return {};
  }
};